var mysql = require('mysql');
var host = 'localhost';
var pool = mysql.createPool({
    connectionLimit: 10,
    host: host,
    user: 'andy',
    password: 'andy.jiang',
    database: 'ipanpan'
});
//db get
exports.get = function get(query, callback) {
    pool.query(query, function (err, rows, fields) {
        if (err) throw err;
        callback(rows);
    });
};
//db set
exports.set = function get(query) {
    pool.query(query, function (err, rows, fields) {
        if (err) throw err;
    });
};